	function jsaveform(url,btn,form,div,grid,index){
	 $.ajax({ 	        
			url: url,
		    type:"POST",
            data: form.serialize(), 
             beforeSend:function(){
                btn.addClass("disabled");
                btn.html("<i class=\"fa fa-refresh\"></i>Guardando...");									 
             },			 
			 error:function(xhr, ajaxOptions, thrownError) {
			    btn.removeClass("disabled");
				btn.html("<i class=\"fa fa-save\"></i>Guardar");
				alert("Error " + xhr.status + ": verifique los datos o contacte el administrador del sistema.");				
			 },			 
			 success:function(data)
		     {
              if(data != "")
				{
					
					if(data!="false")
					{					 
					 btn.removeClass("disabled");				 
					 btn.html("<i class=\"fa fa-save\"></i>Guardar");
                     form[0].reset();
				     div.hide();
				     index.show();
					 $.pjax.reload({container:grid});				 
					}else{
						  if(data == "false"){
							 btn.removeClass("disabled");
							 btn.html("<i class=\"fa fa-save\"></i>Guardar");
							 alert("Ocurrio algun error, verifique los datos e intente nuevamente");
						   }					   
						 }																				
				}else{
					  alert("Hubo un error, por favor verifique los datos antes de enviar el formulario!!!");
					 }
			 },	       
            });	 
	}
	
